from z3 import *

# DECLARATIONS
chalets = ['J', 'K', 'L', 'M', 'N', 'O']
indices = {c: i for i, c in enumerate(chalets)}

# There are 5 segments, each connects exactly two chalets
# Represent each segment as a pair of chalets (ordered by index)
# We'll represent the graph as adjacency lists: for each chalet, a list of connected chalets

# Create adjacency matrix: adj[i][j] = Bool indicating if segment connects chalet i and j
adj = [[Bool(f"adj_{chalets[i]}_{chalets[j]}") for j in range(len(chalets))] for i in range(len(chalets))]

solver = Solver()

# CONSTRAINTS

# 1) The path is composed of 5 straight segments, each connects exactly two chalets
# So total number of True in adj matrix (upper triangle only) is 5
edges = []
for i in range(len(chalets)):
    for j in range(i+1, len(chalets)):
        edges.append(adj[i][j])
solver.add(Sum([If(e, 1, 0) for e in edges]) == 5)

# 2) Each segment connects exactly two chalets (already encoded by adjacency matrix)

# 3) Each chalet is directly connected by a segment to another chalet (degree at least 1)
# But from problem, path connects all chalets, so each chalet has degree at least 1
# Also, no chalet is connected to more than two other chalets (degree <= 2)
for i in range(len(chalets)):
    degree = []
    for j in range(len(chalets)):
        if i == j:
            continue
        # adjacency symmetric: adj[i][j] or adj[j][i]
        if i < j:
            degree.append(If(adj[i][j], 1, 0))
        else:
            degree.append(If(adj[j][i], 1, 0))
    solver.add(And(Sum(degree) >= 1, Sum(degree) <= 2))

# 4) No segment crosses any other segment
# The chalets are arranged in two rows:
# Row 1: J(0), K(1), L(2)
# Row 2: M(3), N(4), O(5)
# Positions:
# J(0) opposite M(3)
# K(1) opposite N(4)
# L(2) opposite O(5)

# Coordinates for checking crossing:
# Row 1: y=1, x = 0,1,2 for J,K,L
# Row 2: y=0, x = 0,1,2 for M,N,O
pos = {
    'J': (0, 1),
    'K': (1, 1),
    'L': (2, 1),
    'M': (0, 0),
    'N': (1, 0),
    'O': (2, 0)
}

def segments_cross(p1, p2, p3, p4):
    # Check if segments p1-p2 and p3-p4 cross
    # p1,p2,p3,p4 are (x,y) tuples
    def ccw(a,b,c):
        return (c[1]-a[1])*(b[0]-a[0]) > (b[1]-a[1])*(c[0]-a[0])
    return And(
        Or(ccw(p1,p3,p4) != ccw(p2,p3,p4), ccw(p1,p2,p3) != ccw(p1,p2,p4))
    )

# For each pair of edges, if they share no chalet, their segments must not cross
for i1 in range(len(chalets)):
    for j1 in range(i1+1, len(chalets)):
        for i2 in range(len(chalets)):
            for j2 in range(i2+1, len(chalets)):
                # edges must be distinct and no shared endpoint
                if len({i1,j1,i2,j2}) == 4:
                    # If both edges exist, then their segments do not cross
                    cross = segments_cross(pos[chalets[i1]], pos[chalets[j1]], pos[chalets[i2]], pos[chalets[j2]])
                    solver.add(Or(Not(adj[i1][j1]), Not(adj[i2][j2]), Not(cross)))

# 5) The path is continuous and connects all chalets
# The graph formed by edges is a path with 6 nodes and 5 edges
# So the graph is connected and has no cycles
# We can check connectivity by ensuring there is a path from J to every other chalet
# Also, since degree <= 2, and edges = 5, the graph is a path (chain)

# Implement connectivity check using reachability via adjacency matrix
# Use Floyd-Warshall or similar for reachability with Bool variables

# Create reachability matrix reach[i][j]: Bool, true if node j reachable from i
reach = [[Bool(f"reach_{i}_{j}") for j in range(len(chalets))] for i in range(len(chalets))]

# Initialize reachability: each node reachable from itself
for i in range(len(chalets)):
    solver.add(reach[i][i])

# For each edge, add reachability in both directions
for i in range(len(chalets)):
    for j in range(len(chalets)):
        if i != j:
            # If edge exists between i and j, then i reaches j and j reaches i
            if i < j:
                edge = adj[i][j]
            else:
                edge = adj[j][i]
            solver.add(Implies(edge, And(reach[i][j], reach[j][i])))

# Floyd-Warshall transitive closure constraints
for k in range(len(chalets)):
    for i in range(len(chalets)):
        for j in range(len(chalets)):
            solver.add(Implies(And(reach[i][k], reach[k][j]), reach[i][j]))

# All chalets reachable from J (index 0)
for i in range(len(chalets)):
    solver.add(reach[0][i])

# 6) One segment connects J and N
solver.add(adj[indices['J']][indices['N']] if indices['J'] < indices['N'] else adj[indices['N']][indices['J']])

# 7) Another segment connects K and L
solver.add(adj[indices['K']][indices['L']] if indices['K'] < indices['L'] else adj[indices['L']][indices['K']])

# 8) Given that one segment connects K and O
solver.add(adj[indices['K']][indices['O']] if indices['K'] < indices['O'] else adj[indices['O']][indices['K']])

# OPTION CHECKING

options = [
    ('A', (indices['J'], indices['M'])),  # Chalet J is directly connected to chalet M
    ('B', (indices['K'], indices['N'])),  # Chalet K is directly connected to chalet N
    ('C', (indices['L'], indices['O'])),  # Chalet L is directly connected to chalet O
    ('D', 'L_two_connections'),           # Chalet L is directly connected to exactly two chalets
    ('E', 'O_one_connection')              # Chalet O is directly connected to exactly one chalet
]

for letter, cond in options:
    solver.push()
    if letter == 'D':
        # L connected to exactly two chalets
        degree_L = []
        i = indices['L']
        for j in range(len(chalets)):
            if i == j:
                continue
            if i < j:
                degree_L.append(If(adj[i][j], 1, 0))
            else:
                degree_L.append(If(adj[j][i], 1, 0))
        solver.add(Sum(degree_L) == 2)
    elif letter == 'E':
        # O connected to exactly one chalet
        degree_O = []
        i = indices['O']
        for j in range(len(chalets)):
            if i == j:
                continue
            if i < j:
                degree_O.append(If(adj[i][j], 1, 0))
            else:
                degree_O.append(If(adj[j][i], 1, 0))
        solver.add(Sum(degree_O) == 1)
    else:
        # direct connection between two chalets
        i, j = cond
        if i < j:
            solver.add(adj[i][j])
        else:
            solver.add(adj[j][i])
    if solver.check() == sat:
        print(f"'{letter}'")
        solver.pop()
        break
    solver.pop()

from z3 import *

# DECLARATIONS
apprentices = ['Louis', 'Madelyn', 'Nora', 'Oliver']
projects = ['Q', 'R', 'S', 'T']

# Initial assignments (indices of projects)
initial_assignment = {
    'Louis': projects.index('Q'),
    'Madelyn': projects.index('R'),
    'Nora': projects.index('S'),
    'Oliver': projects.index('T')
}

# Variables for assignments after one reassignment
assignment = {}
for a in apprentices:
    assignment[a] = Int(a)

# CONSTRAINTS
solver = Solver()

# Each apprentice assigned to a valid project
for a in apprentices:
    solver.add(And(assignment[a] >= 0, assignment[a] < len(projects)))

# Only one reassignment applied: Plan 1, Plan 2, or Plan 3
# Define the result of applying each plan once

# Plan 1: Q<->S and R<->T
plan1 = {}
for a in apprentices:
    if initial_assignment[a] == projects.index('Q'):
        plan1[a] = projects.index('S')
    elif initial_assignment[a] == projects.index('S'):
        plan1[a] = projects.index('Q')
    elif initial_assignment[a] == projects.index('R'):
        plan1[a] = projects.index('T')
    elif initial_assignment[a] == projects.index('T'):
        plan1[a] = projects.index('R')

# Plan 2: S<->T
plan2 = {}
for a in apprentices:
    if initial_assignment[a] == projects.index('S'):
        plan2[a] = projects.index('T')
    elif initial_assignment[a] == projects.index('T'):
        plan2[a] = projects.index('S')
    else:
        plan2[a] = initial_assignment[a]

# Plan 3: Louis<->Madelyn switch projects
plan3 = {}
for a in apprentices:
    if a == 'Louis':
        plan3[a] = initial_assignment['Madelyn']
    elif a == 'Madelyn':
        plan3[a] = initial_assignment['Louis']
    else:
        plan3[a] = initial_assignment[a]

# Possible assignments after one reassignment
plans = [plan1, plan2, plan3]

# OPTION CHECKING
options = {
    'A': assignment['Louis'] == projects.index('T'),
    'B': assignment['Nora'] == projects.index('R'),
    'C': assignment['Oliver'] == projects.index('Q'),
    'D': And(assignment['Louis'] == initial_assignment['Louis'], assignment['Nora'] == initial_assignment['Nora']),
    'E': And(assignment['Nora'] == initial_assignment['Nora'], assignment['Oliver'] == initial_assignment['Oliver']),
}

for i, (opt_letter, opt_constraint) in enumerate(options.items()):
    solver.push()
    # The assignment must be equal to one of the plans after one reassignment
    plan_constraints = []
    for plan in plans:
        equalities = [assignment[a] == plan[a] for a in apprentices]
        plan_constraints.append(And(equalities))
    solver.add(Or(plan_constraints))
    solver.add(opt_constraint)
    if solver.check() == sat:
        print(f"'{opt_letter}'")
        solver.pop()
        break
    solver.pop()

from z3 import *

# DECLARATIONS
students = ['Kim', 'Lee', 'Pat', 'Robin', 'Sandy', 'Terry', 'Val']
years = {'Kim':4, 'Lee':4, 'Pat':3, 'Robin':3, 'Sandy':2, 'Terry':2, 'Val':2}

# Room size options for each option letter
option_room_sizes = {
    'A': [3,3,1],         # two triples and one single
    'B': [3,1,1,1,1],     # one triple and four singles
    'C': [2,2,2,1],       # three doubles and a single
    'D': [2,2,1,1,1],     # two doubles and three singles
    'E': [2,1,1,1,1,1]    # one double and five singles
}

# Maximum rooms needed is 6 (for option E)
max_rooms = 6

# Create variables: room assignment for each student (integer from 0 to max_rooms-1)
room = {s: Int(s) for s in students}

# CONSTRAINTS
solver = Solver()

# Each student's room must be between 0 and max_rooms-1
for s in students:
    solver.add(And(room[s] >= 0, room[s] < max_rooms))

# Helper function to get list of students in a given room for a model
def students_in_room(model, r):
    return [s for s in students if model.eval(room[s]).as_long() == r]

# OPTION CHECKING
for option_letter in ['A', 'B', 'C', 'D', 'E']:
    solver.push()
    roomsizes = option_room_sizes[option_letter]
    n_rooms = len(roomsizes)

    # Restrict room assignments to only rooms 0..n_rooms-1
    for s in students:
        solver.add(room[s] < n_rooms)

    # For each room, count how many students assigned to it
    for r in range(n_rooms):
        count_in_room = Sum([If(room[s] == r, 1, 0) for s in students])
        # Room size must match one of the sizes in roomsizes at position r
        # So count_in_room == roomsizes[r]
        solver.add(count_in_room == roomsizes[r])

    # No fourth-year student can be assigned to a triple (room of size 3)
    for r in range(n_rooms):
        if roomsizes[r] == 3:
            for s in students:
                if years[s] == 4:
                    solver.add(room[s] != r)

    # No second-year student can be assigned to a single (room of size 1)
    for r in range(n_rooms):
        if roomsizes[r] == 1:
            for s in students:
                if years[s] == 2:
                    solver.add(room[s] != r)

    # Lee and Robin must not share the same room
    solver.add(room['Lee'] != room['Robin'])

    # Kim and Pat must share the same room
    solver.add(room['Kim'] == room['Pat'])

    # Check satisfiability
    if solver.check() == sat:
        print(f"'{option_letter}'")
        solver.pop()
        break
    solver.pop()

