# DECLARATIONS
employees = ['Maria', 'Suki', 'Tate']
days = [0, 1, 2]  # 0=Day1, 1=Day2, 2=Day3
topics = ['Hiring', 'Investing', 'Regulations']

num_employees = len(employees)
num_days = len(days)
num_topics = len(topics)

# Create variables: attendance[e][d][t] = Bool indicating if employee e attends session on day d, topic t
attendance = {}
for e in employees:
    for d in days:
        for t in topics:
            attendance[(e, d, t)] = Bool(f"{e}_{d}_{t}")

solver = Solver()

# CONSTRAINTS

# 1) Each employee attends exactly two sessions
for e in employees:
    sess = [attendance[(e, d, t)] for d in days for t in topics]
    solver.add(Sum([If(s, 1, 0) for s in sess]) == 2)

# 2) The two sessions attended by each employee are on different days and different topics
for e in employees:
    # For all pairs of sessions, if both attended, days and topics differ
    for d1 in days:
        for t1 in topics:
            for d2 in days:
                for t2 in topics:
                    if d1 == d2 and t1 == t2:
                        continue
                    # If both sessions attended, then d1 != d2 and t1 != t2
                    # So if both attended, then d1 != d2 and t1 != t2
                    # Contrapositive: if d1 == d2 or t1 == t2 then not both attended
                    same_day = (d1 == d2)
                    same_topic = (t1 == t2)
                    if same_day or same_topic:
                        solver.add(Or(Not(attendance[(e, d1, t1)]), Not(attendance[(e, d2, t2)])))

# 3) Neither Maria nor Suki attends any session on investing
for e in ['Maria', 'Suki']:
    for d in days:
        solver.add(attendance[(e, d, 'Investing')] == False)

# 4) Tate does not attend any session on the third day (day index 2)
for t in topics:
    solver.add(attendance[('Tate', 2, t)] == False)

# 5) At most two Capital employees attend any given session
for d in days:
    for t in topics:
        sess_attendance = [attendance[(e, d, t)] for e in employees]
        solver.add(Sum([If(s, 1, 0) for s in sess_attendance]) <= 2)

# Helper function to check option possibility
def check_option(option_number):
    solver.push()
    if option_number == 0:  # (A) Every session attended by at least one Capital employee is attended by exactly one Capital employee.
        # For all sessions, if attended by at least one employee, then exactly one employee attends
        for d in days:
            for t in topics:
                sess_attendance = [attendance[(e, d, t)] for e in employees]
                count = Sum([If(s, 1, 0) for s in sess_attendance])
                # count == 0 or count == 1
                solver.add(Or(count == 0, count == 1))
    elif option_number == 1:  # (B) Every session attended by at least one Capital employee is attended by exactly two Capital employees.
        for d in days:
            for t in topics:
                sess_attendance = [attendance[(e, d, t)] for e in employees]
                count = Sum([If(s, 1, 0) for s in sess_attendance])
                # count == 0 or count == 2
                solver.add(Or(count == 0, count == 2))
    elif option_number == 2:  # (C) Every session attended by Maria is also attended by Suki.
        for d in days:
            for t in topics:
                solver.add(Implies(attendance[('Maria', d, t)], attendance[('Suki', d, t)]))
    elif option_number == 3:  # (D) Every session attended by Suki is also attended by Tate.
        for d in days:
            for t in topics:
                solver.add(Implies(attendance[('Suki', d, t)], attendance[('Tate', d, t)]))
    elif option_number == 4:  # (E) Every session attended by Tate is also attended by Maria.
        for d in days:
            for t in topics:
                solver.add(Implies(attendance[('Tate', d, t)], attendance[('Maria', d, t)]))
    else:
        solver.pop()
        return False

    result = solver.check()
    solver.pop()
    return result == sat

