# DECLARATIONS
employees = ['Maria', 'Suki', 'Tate']
days = [0, 1, 2]  # 0: first day, 1: second day, 2: third day
topics = ['hiring', 'investing', 'regulations']

num_employees = len(employees)
num_days = len(days)
num_topics = len(topics)

# Create variables: attendance[employee][day][topic] = Bool indicating if employee attends that session
attendance = {}
for e in employees:
    attendance[e] = {}
    for d in days:
        attendance[e][d] = {}
        for t in topics:
            attendance[e][d][t] = Bool(f"{e}_{d}_{t}")

solver = Solver()

# CONSTRAINTS

# 1) Each participant attends exactly two sessions
for e in employees:
    sessions_attended = [attendance[e][d][t] for d in days for t in topics]
    solver.add(Sum([If(s, 1, 0) for s in sessions_attended]) == 2)

# 2) The two sessions each participant attends are on different topics and different days
for e in employees:
    # Extract all sessions attended as a list of (day, topic, Bool)
    sessions = [(d, t, attendance[e][d][t]) for d in days for t in topics]
    # For every pair of sessions attended, if both attended, then days and topics differ
    for i in range(len(sessions)):
        for j in range(i+1, len(sessions)):
            d1, t1, s1 = sessions[i]
            d2, t2, s2 = sessions[j]
            # If both attended, days differ and topics differ
            solver.add(Implies(And(s1, s2), And(d1 != d2, t1 != t2)))

# 3) Neither Maria nor Suki attends any session on investing
for e in ['Maria', 'Suki']:
    for d in days:
        solver.add(attendance[e][d]['investing'] == False)

# 4) Tate does not attend any session on the third day (day 2)
for tpc in topics:
    solver.add(attendance['Tate'][2][tpc] == False)

# 5) At most two Capital employees attend any given session together
for d in days:
    for t in topics:
        attendees = [attendance[e][d][t] for e in employees]
        solver.add(Sum([If(a, 1, 0) for a in attendees]) <= 2)

# 6) All three sessions on the first day are attended by one or more Capital employees
for t in topics:
    attendees = [attendance[e][0][t] for e in employees]
    solver.add(Or([a for a in attendees]))

# OPTION CHECKING

# Define option-specific constraints as functions that return BoolRef (constraints to add)
def option_A():
    # (A) Maria and Suki attend a session together on the third day.
    # There exists a topic on day 2 (third day) such that Maria and Suki both attend it
    conditions = []
    for t in topics:
        conditions.append(And(attendance['Maria'][2][t], attendance['Suki'][2][t]))
    return Or(conditions)

def option_B():
    # (B) Suki and Tate attend a session together on the second day.
    # There exists a topic on day 1 (second day) such that Suki and Tate both attend it
    conditions = []
    for t in topics:
        conditions.append(And(attendance['Suki'][1][t], attendance['Tate'][1][t]))
    return Or(conditions)

def option_C():
    # (C) Maria attends a session on hiring on the second day.
    return attendance['Maria'][1]['hiring']

def option_D():
    # (D) Suki attends a session on regulations on the third day.
    return attendance['Suki'][2]['regulations']

def option_E():
    # (E) Tate attends a session on investing on the first day.
    return attendance['Tate'][0]['investing']

options = [option_A, option_B, option_C, option_D, option_E]

# We want to find which option must be false.
# So for each option:
#   Add the option constraint (assume it is true)
#   Check if the model is satisfiable with all constraints plus the option
#   If unsat, then the option must be false.

for i, opt_func in enumerate(options):
    solver.push()
    solver.add(opt_func())
    if solver.check() == unsat:
        print(f"'{chr(65+i)}'")
