from constraint import Problem, AllDifferentConstraint

def solve_einstein_puzzle():
    problem = Problem()
    num_positions = 3

    categories = ["Pet", "Job"]
    items = {
        "Pet": ["dog", "turtle", "ferret"],
        "Job": ["journalist", "scientist", "dressmaker"]
    }

    # Add variables
    for category in categories:
        for item in items[category]:
            problem.addVariable(f"{category}:{item}", range(num_positions))

    # All items in the same category must be in different positions
    for category in categories:
        problem.addConstraint(AllDifferentConstraint(), [f"{category}:{item}" for item in items[category]])

    # Clue 1: turtle immediately to the left of dressmaker
    problem.addConstraint(
        lambda turtle, dressmaker: turtle == dressmaker - 1,
        ["Pet:turtle", "Job:dressmaker"]
    )

    # Clue 2: scientist immediately to the right of ferret
    problem.addConstraint(
        lambda scientist, ferret: scientist == ferret + 1,
        ["Job:scientist", "Pet:ferret"]
    )

    # Clue 3: scientist is directly between ferret and dog
    problem.addConstraint(
        lambda scientist, ferret, dog: (scientist == ferret + 1 and scientist == dog - 1) or (scientist == dog + 1 and scientist == ferret - 1),
        ["Job:scientist", "Pet:ferret", "Pet:dog"]
    )

    solutions = problem.getSolutions()
    if not solutions:
        return None

    solution = solutions[0]

    result_dict = {}
    for category in categories:
        result_dict[category] = [""] * num_positions
        for item in items[category]:
            pos = solution[f"{category}:{item}"]
            result_dict[category][pos] = item

    print(result_dict)

solve_einstein_puzzle()
